import React from "react";
import Timer from "./components/Timer";
import Scheduler from "./components/CalendarScheduler";

const App = () => {
  const userEmail = "testuser@example.com"; // Replace with dynamic auth

  return (
    <div className="min-h-screen flex flex-col items-center justify-center bg-background text-white p-6">
      <h1 className="text-4xl font-bold text-primary mb-6">Pomodoro Planner</h1>
      <div className="bg-secondary p-8 rounded-2xl shadow-lg">
        <Timer />
      </div>
      <div className="mt-6 w-full max-w-3xl">
        <Scheduler userEmail={userEmail} />
      </div>
    </div>
  );
};

export default App;
