import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";
import runningSprite from "/src/assets/running-girl-sprite.png"; // ✅ Ensure correct path

const Timer = () => {
  const userName = "Lulia";
  const [workDuration, setWorkDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [cycles, setCycles] = useState(4);
  const [isRunning, setIsRunning] = useState(false);
  const [isWorkSession, setIsWorkSession] = useState(true);
  const [timeLeft, setTimeLeft] = useState(workDuration * 60);
  const [completedCycles, setCompletedCycles] = useState(0);
  const [autoStart, setAutoStart] = useState(false);
  const [message, setMessage] = useState("");
  const [showConfetti, setShowConfetti] = useState(false);
  const [cheering, setCheering] = useState(false); // To trigger cheering animation


  const focusMessages = [
    `You got this, ${userName}!`,
    `Stay focused, ${userName}!`,
    `Keep pushing forward, ${userName}!`,
    `You're doing amazing, ${userName}!`,
  ];

  const breakMessages = [
    `Take a deep breath, ${userName}!`,
    `Relax and recharge, ${userName}!`,
    `You’ve earned this break, ${userName}!`,
  ];

  useEffect(() => {
    if (!isRunning) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    if (timeLeft % 10 === 0 && timeLeft !== 0) {
      const messages = isWorkSession ? focusMessages : breakMessages;
      setMessage(messages[Math.floor(Math.random() * messages.length)]);
    }

    if (timeLeft <= 0) {
      clearInterval(timer);
      handleSessionEnd();
    }

    return () => clearInterval(timer);
  }, [isRunning, timeLeft]);

  const handleSessionEnd = () => {
    if (isWorkSession) {
      setCompletedCycles((prev) => prev + 1);
      setShowConfetti(true);
      setCheering(true); // Trigger the cheering animation

      setTimeout(() => {
        setShowConfetti(false);
        setCheering(false);
      }, 5000);

      if (completedCycles + 1 >= cycles) {
        alert(`All Pomodoro cycles completed! Take a long break, ${userName}.`);
        setIsRunning(false);
        setCompletedCycles(0);
      } else {
        setIsWorkSession(false);
        setTimeLeft(breakDuration * 60);
      }
    } else {
      setIsWorkSession(true);
      setTimeLeft(workDuration * 60);
    }

    if (autoStart) {
      setIsRunning(true);
    }
  };

  const resetTimer = () => {
    setIsRunning(false);
    setIsWorkSession(true);
    setTimeLeft(workDuration * 60);
    setCompletedCycles(0);
    setMessage("");
    setShowConfetti(false);
    setCheering(false);
  };

  const progress = isWorkSession
    ? (timeLeft / (workDuration * 60)) * 100
    : (timeLeft / (breakDuration * 60)) * 100;

  return (
    <div className="timer-container">
      {showConfetti && <Confetti />}
      <h2 className="timer-title">{isWorkSession ? "Focus Time" : "Break Time"}</h2>
      <h1 className="timer">
        {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
      </h1>

      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        <img
          src={runningSprite}
          alt="Running Girl"
          className={`running-girl ${cheering ? "cheering" : ""}`}
          style={{ left: `${progress}%` }}
        />
      </div>

      {message && <p className="speech-bubble">{message}</p>}

      <div className="timer-controls">
        <button onClick={() => setIsRunning(!isRunning)}>
          {isRunning ? "Pause" : "Start"}
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>
    </div>
  );
};

export default Timer;
