import React, { useState, useEffect } from "react";

const Timer = () => {
  const userName = "Lulia"; // Can be dynamic in future
  const [workDuration, setWorkDuration] = useState(25); // Default 25 min
  const [breakDuration, setBreakDuration] = useState(5); // Default 5 min
  const [cycles, setCycles] = useState(4); // Default 4 cycles
  const [isRunning, setIsRunning] = useState(false);
  const [isWorkSession, setIsWorkSession] = useState(true);
  const [timeLeft, setTimeLeft] = useState(workDuration * 60);
  const [completedCycles, setCompletedCycles] = useState(0);
  const [autoStart, setAutoStart] = useState(false);
  const [message, setMessage] = useState(""); // Encouraging messages

  const messages = [
    `You got this, ${userName}!`,
    `Stay focused, ${userName}!`,
    `Keep pushing, ${userName}!`,
    `You're doing great, ${userName}!`,
    `Almost there, ${userName}!`,
    `Don't give up, ${userName}!`,
  ];

  useEffect(() => {
    if (isWorkSession) {
      setTimeLeft(workDuration * 60);
    } else {
      setTimeLeft(breakDuration * 60);
    }
  }, [workDuration, breakDuration, isWorkSession]);

  useEffect(() => {
    if (!isRunning) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    if (timeLeft % 60 === 0 && timeLeft !== 0) {
      setMessage(messages[Math.floor(Math.random() * messages.length)]);
    }

    if (timeLeft <= 0) {
      clearInterval(timer);
      handleSessionEnd();
    }

    return () => clearInterval(timer);
  }, [isRunning, timeLeft]);

  const handleSessionEnd = () => {
    if (isWorkSession) {
      setCompletedCycles((prev) => prev + 1);
      if (completedCycles + 1 >= cycles) {
        alert(`All Pomodoro cycles completed! Take a long break, ${userName}.`);
        setIsRunning(false);
        setCompletedCycles(0);
      } else {
        setIsWorkSession(false);
        setTimeLeft(breakDuration * 60);
      }
    } else {
      setIsWorkSession(true);
      setTimeLeft(workDuration * 60);
    }

    if (autoStart) {
      setIsRunning(true);
    }
  };

  const resetTimer = () => {
    setIsRunning(false);
    setIsWorkSession(true);
    setTimeLeft(workDuration * 60);
    setCompletedCycles(0);
    setMessage("");
  };

  // Progress bar calculation
  const progress = isWorkSession
    ? (timeLeft / (workDuration * 60)) * 100
    : (timeLeft / (breakDuration * 60)) * 100;

  return (
    <div className="timer-container">
      <h2 className="timer-title">
        {isWorkSession ? "Focus Time" : "Break Time"}
      </h2>
      <h1 className="timer">
        {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
      </h1>
      
      {/* Progress Bar */}
      <div className="progress-bar-container">
        <div
          className="progress-bar"
          style={{ width: `${progress}%` }}
        ></div>
      </div>

      {/* Encouraging Message */}
      <p className="encouraging-message">{message}</p>

      <div className="timer-controls">
        <button onClick={() => setIsRunning(!isRunning)}>
          {isRunning ? "Pause" : "Start"}
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>

      <div className="session-settings">
        <label>Work Duration:</label>
        <input
          type="number"
          value={workDuration}
          onChange={(e) => setWorkDuration(parseInt(e.target.value) || 25)}
        />{" "}
        min

        <label>Break Duration:</label>
        <input
          type="number"
          value={breakDuration}
          onChange={(e) => setBreakDuration(parseInt(e.target.value) || 5)}
        />{" "}
        min

        <label>Pomodoro Cycles:</label>
        <input
          type="number"
          value={cycles}
          onChange={(e) => setCycles(parseInt(e.target.value) || 4)}
        />
      </div>
      <div className="auto-start">
        <label>Auto Start Next Session?</label>
        <input
          type="checkbox"
          checked={autoStart}
          onChange={() => setAutoStart(!autoStart)}
        />
      </div>
      <p className="session-progress">
        Completed Cycles: {completedCycles}/{cycles}
      </p>
    </div>
  );
};

export default Timer;
