import React, { useState, useEffect } from "react";

const Timer = () => {
  const [workDuration, setWorkDuration] = useState(25 * 60); // Default 25 min
  const [breakDuration, setBreakDuration] = useState(5 * 60); // Default 5 min
  const [cycles, setCycles] = useState(4); // Default: 4 work sessions
  const [isRunning, setIsRunning] = useState(false);
  const [isWorkSession, setIsWorkSession] = useState(true);
  const [timeLeft, setTimeLeft] = useState(workDuration);
  const [completedCycles, setCompletedCycles] = useState(0);
  const [autoStart, setAutoStart] = useState(false);

  useEffect(() => {
    if (!isRunning) return;

    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);

    if (timeLeft <= 0) {
      clearInterval(timer);
      handleSessionEnd();
    }

    return () => clearInterval(timer);
  }, [isRunning, timeLeft]);

  const handleSessionEnd = () => {
    if (isWorkSession) {
      setCompletedCycles((prev) => prev + 1);
      if (completedCycles + 1 >= cycles) {
        alert("All Pomodoro cycles completed! Take a long break.");
        setIsRunning(false);
        setCompletedCycles(0);
      } else {
        setTimeLeft(breakDuration);
        setIsWorkSession(false);
      }
    } else {
      setTimeLeft(workDuration);
      setIsWorkSession(true);
    }

    if (autoStart) {
      setIsRunning(true);
    }
  };

  const resetTimer = () => {
    setIsRunning(false);
    setIsWorkSession(true);
    setTimeLeft(workDuration);
    setCompletedCycles(0);
  };

  return (
    <div className="timer-container">
      <h2 className="timer-title">
        {isWorkSession ? "Focus Time" : "Break Time"}
      </h2>
      <h1 className="timer">
        {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
      </h1>
      <div className="timer-controls">
        <button onClick={() => setIsRunning(!isRunning)}>
          {isRunning ? "Pause" : "Start"}
        </button>
        <button onClick={resetTimer}>Reset</button>
      </div>
      <div className="session-settings">
        <label>Work Duration:</label>
        <input
          type="number"
          value={workDuration / 60}
          onChange={(e) => setWorkDuration(e.target.value * 60)}
        /> min

        <label>Break Duration:</label>
        <input
          type="number"
          value={breakDuration / 60}
          onChange={(e) => setBreakDuration(e.target.value * 60)}
        /> min

        <label>Pomodoro Cycles:</label>
        <input
          type="number"
          value={cycles}
          onChange={(e) => setCycles(parseInt(e.target.value))}
        />
      </div>
      <div className="auto-start">
        <label>Auto Start Next Session?</label>
        <input
          type="checkbox"
          checked={autoStart}
          onChange={() => setAutoStart(!autoStart)}
        />
      </div>
      <p className="session-progress">
        Completed Cycles: {completedCycles}/{cycles}
      </p>
    </div>
  );
};

export default Timer;
