import React, { useState, useEffect } from "react";

const Timer = () => {
  const [timeLeft, setTimeLeft] = useState(25 * 60);
  const [isRunning, setIsRunning] = useState(false);

  useEffect(() => {
    if (!isRunning) return;
    const timer = setInterval(() => {
      setTimeLeft((prev) => prev - 1);
    }, 1000);
    return () => clearInterval(timer);
  }, [isRunning]);

  return (
    <div className="text-center">
      <h2 className="text-xl font-semibold text-accent mb-4">Pomodoro Timer</h2>
      <div className="text-6xl font-bold p-6 bg-black bg-opacity-20 rounded-lg shadow-md">
        {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
      </div>
      <div className="mt-4 flex justify-center gap-4">
        <button
          onClick={() => setIsRunning(!isRunning)}
          className="bg-primary px-6 py-2 rounded-xl shadow-lg text-white font-semibold hover:bg-opacity-80 transition"
        >
          {isRunning ? "Pause" : "Start"}
        </button>
        <button
          onClick={() => setTimeLeft(25 * 60)}
          className="bg-red-500 px-6 py-2 rounded-xl shadow-lg text-white font-semibold hover:bg-opacity-80 transition"
        >
          Reset
        </button>
      </div>
    </div>
  );
};

export default Timer;
