import React, { useState, useEffect } from "react";
import Confetti from "react-confetti";
import styles from "./Timer.module.css"; // ✅ Import CSS Module
import runningSprite from "../assets/running-girl-sprite.png"; // ✅ Import image

const Timer = () => {
  const userName = "Lulia";
  const [workDuration, setWorkDuration] = useState(25);
  const [breakDuration, setBreakDuration] = useState(5);
  const [cycles, setCycles] = useState(4);
  const [isRunning, setIsRunning] = useState(false);
  const [isWorkSession, setIsWorkSession] = useState(true);
  const [timeLeft, setTimeLeft] = useState(workDuration * 60);
  const [completedCycles, setCompletedCycles] = useState(0);
  const [autoStart, setAutoStart] = useState(false);
  const [message, setMessage] = useState("");
  const [showConfetti, setShowConfetti] = useState(false);
  const [cheering, setCheering] = useState(false);

  const progress = isWorkSession
    ? (timeLeft / (workDuration * 60)) * 100
    : (timeLeft / (breakDuration * 60)) * 100;

  return (
    <div className="timer-container">
      {showConfetti && <Confetti />}
      <h2 className="timer-title">{isWorkSession ? "Focus Time" : "Break Time"}</h2>
      <h1 className="timer">
        {Math.floor(timeLeft / 60)}:{String(timeLeft % 60).padStart(2, "0")}
      </h1>

      <div className="progress-bar-container">
        <div className="progress-bar" style={{ width: `${progress}%` }}></div>
        <img
          src={runningSprite} // ✅ Use imported image
          alt="Running Girl"
          className={styles.runningGirl} // ✅ Use CSS module
          style={{ left: `${progress}%` }}
        />
      </div>

      <div className="timer-controls">
        <button onClick={() => setIsRunning(!isRunning)}>
          {isRunning ? "Pause" : "Start"}
        </button>
      </div>
    </div>
  );
};

export default Timer;
